DROP TABLE IF EXISTS `wxm_resource`;
CREATE TABLE `wxm_resource`  (
  `resource_id` varchar(64) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '主键',
  `resource_type` varchar(1) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '资源类型：1节点 2 叶子 3 按钮',
