  UNIQUE INDEX `uidx_wxm_resource_resource_name`(`resource_name`) USING BTREE,
  UNIQUE INDEX `uidx_wxm_resource_resource_url`(`resource_url`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 40026 CHARACTER SET = utf8mb3 COLLATE = utf8mb3_general_ci COMMENT = '资源表' ROW_FORMAT = Dynamic;

