DROP TABLE IF EXISTS `wxm_user`;
CREATE TABLE `wxm_user`  (
  `user_id` varchar(64) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '主键',
  `username` varchar(50) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '用户名',
  `passwd` varchar(200) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '密码，密文',
  `user_type` varchar(15) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '用户类别：U-SUPER-ADMIN 超级管理员，U-ADMIN 系统管理员，U-MANAGER 系统管理员，U-BIZ 业务员，U-ORDINARY 普通用户 ',
  `user_rate` varchar(15) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '用户级别：NON-MEMBER 普通会员，SILVER-MEMBER 白银会员 GOLD-MEMBER 黄金会员 PLATINUM-MEMBER 白金会员 DIAMOND-MEMBER 钻石会员',
  `user_status` varchar(1) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '0 无效，1 正常，2 注销，3锁定',
  `account_expired_time` datetime(0) NULL DEFAULT NULL COMMENT '账号过期时间',
  `password_expired_time` datetime(0) NULL DEFAULT NULL COMMENT '密码过期时间',
  `lock_time` datetime(0) NULL DEFAULT NULL COMMENT '账号锁定时间',
  `unlock_time` datetime(0) NULL DEFAULT NULL COMMENT '账号解锁时间',
  `user_desc` varchar(500) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '描述',
  `operator` varchar(50) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '最近一次操作者',
  `creator` varchar(50) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '创建者',
  `create_time` datetime(0) NULL DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime(0) NULL DEFAULT NULL COMMENT '更新时间',
  `phone_number` varchar(15) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '联系电话',
  `facebook_id` varchar(50) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT 'Facebook ID（社交账号关联）',
  `wechat_openid` varchar(50) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '微信OpenID（社交账号关联）',
  `tel` varchar(15) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '座机',
  `email` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '邮箱',
  `avatar_url` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '头像URL',
  `nickname` varchar(50) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '昵称',
  `gender` enum('male','female','other') CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '性别',
  `avatar_resource_id` int(0) UNSIGNED NULL DEFAULT NULL COMMENT '头像资源ID',
  `birthday` date NULL DEFAULT NULL COMMENT '生日',
  `registration_ip` varchar(45) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL DEFAULT NULL COMMENT '注册IP',
  `register_time` timestamp(0) NULL DEFAULT CURRENT_TIMESTAMP COMMENT '注册时间',
  `last_login` timestamp(0) NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '最后登录时间',
  `membership_point` int(0) NULL DEFAULT 0 COMMENT '用户积分',
  PRIMARY KEY (`user_id`) USING BTREE,
  UNIQUE INDEX `uidx_wxm_user_username`(`username`) USING BTREE,
  UNIQUE INDEX `email`(`email`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 10013 CHARACTER SET = utf8mb3 COLLATE = utf8mb3_general_ci COMMENT = '用户表' ROW_FORMAT = Dynamic;

